  const [open, setOpen] = () => {
    useState(false);
  }

  const show = () => {
    setOpen(true);
  }

  return (
    <div className={$hover ? "circle hover" : "circle"} onClick={show}>
      <span className="circleText" title={tooltip}>
        {text}
      </span>
    </div>
  );
};

export default Markers;
