//import { GoogleMap, useLoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import React  from "react";
import "../components/Map.css";
import GoogleMapReact from "google-map-react";
import { useLoaderData } from "react-router-dom";
import { useRecoilValue } from "recoil";
import { runsState } from "../hooks/useAppData";


import Markers from "./Markers";




export default function SimpleMap(){

  const marker = useRecoilValue(runsState);
  console.log(marker)

  const markers = marker.map(({ latitude, longitude, id, name }) => {
    return (
      <Markers key={id} latitude={latitude} longitude={longitude} name={name} />
    );
  })

  console.log(markers)

  // const showMarkers = (marker) => {
  //   return marker.map((m) => <Markers key={m.id} run={m} name={m.name} />)
  // }

  // console.log(showMarkers)

  // const showMarkers = (runs, type) => {
  //   const runsArray = Object.values(runs);
  //   return runsArray.map((run) => <Run key={run.id} run={run} type={type} />);
  // };


  const defaultProps = {
    center: {
      lat: 43,
      lng: -80
    },
    zoom: 7
  };

  const myKey = process.env.REACT_APP_MAP_API_KEY;

  return (
    <div style={{ height: '80vh', width: '80%' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: myKey }}
        defaultCenter={defaultProps.center}
        defaultZoom={defaultProps.zoom}
      >

         {/* {marker.map(({ latitude, longitude, id, name }) => {
          return (
            <Markers key={id} latitude={latitude} longitude={longitude} name={name} />
          );
        })}  */}
      </GoogleMapReact>
    </div>
  );
}
