import axios from 'axios';
import React, { useState, useEffect } from 'react';

//hook for getting runs
//hook for logged in user
//hook for runner only runs
//hook for planner runs
//axios route change based on user id



export default class AppData extends React.Component {

 state = {
    runs: []
  }

  componentDidMount() {
    axios.get('/api/runs') 
    .then(res => {
      const runs = JSON.stringify(res.data);
      this.setState({ runs });
    })
  }


  render() {
    console.log(this.state);

    return (
      <ul>
        {
          this.state.runs.map(r => 
            <li key={r.id}>{r.location}</li>
            )
        }
      </ul>
    )
  }

}
