import axios from 'axios';
import React, { useState, useEffect } from 'react';

//hook for getting runs
//hook for logged in user
//hook for runner only runs
//hook for planner runs
//axios route change based on user id



export default function AppData() {

  const fetchRunData = () => {
    axios.get('/api/runs') // You can simply make your requests to "/api/whatever you want"
    .then((res) => {
      // handle success
      const runs = res.data
      console.log(runs) // The entire response from the Rails API

      console.log(res.data.message) // Just the message
      this.setState({
        runs: runs
      });
    }) 
  }
}
