// export default SimpleMap;
export default function SimpleMap(){

  const setUserCoords = useSetRecoilState(userCoordinatesAtom);

  useEffect(() => {
    const success = (pos) => {
      let coord = pos.coords;
      setUserCoords({ lat: coord.coords, lng: coord.longitude });
    }

    const error = (err) => {
      console.warn(`There was an error:${err.code}, ${err.message}`)
    }

    return navigator.geolocation.getCurrentPosition(success, error);

  }, [setUserCoords])

  const center = useRecoilValue(userCoordinatesAtom);
  //const [center, setCenter] = useState({lat: 43.6532, lng: -79.3832})
  const zoom = 10;

  // useEffect(() => {
  //   getUserCoords({ setCenter });
  // }, [center]);

  return (
    <>
    <div className="map" style={{ height: '80vh', width: '80%' }}>
      <DefaultMap center={center} zoom={zoom} />
    </div>
    </>  

  );
}
