/* eslint-disable no-unused-vars */
import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import FloatingLabel from "react-bootstrap/FloatingLabel";
import "bootstrap/dist/css/bootstrap.min.css";
import "../components/RegisterUser.css";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import DatePicker from "react-datepicker";
import { useLoaderData, useNavigate } from "react-router-dom";
import { useRecoilValue } from "recoil";
import { userState, runsState } from "../hooks/useAppData";
import useAppData from "../hooks/useAppData";
import "react-datepicker/dist/react-datepicker.css";
import AutoComplete from "./AutoComplete";

export default function RegisterRun() {

  const [runData, setRunData] = useState({
    name: "",
    description: "",
    location: "",
    distance: "",
    time: `${new Date().getHours()}:${new Date().getMinutes()}`,
    date: new Date(),
    file: ""
  });


  const [placeName, setPlaceName] = useState("");
  const [address, setAddress] = useState("");
  const [coords, setCoords] = useState({ lat: "", lng: "" });
  const [time, setTime] = useState(
    `${new Date().getHours()}:${new Date().getMinutes()}`
  );
  const [date, setDate] = useState(new Date());

  const user = useRecoilValue(userState);
  const navigate = useNavigate();
  const { createRun } = useAppData();


  const handleChange = (e) => {
    setRunData({...runData, [e.target.name]: e.target.value });
  }

  const handleCheckboxChange = (e) => {
    const prev = runData[e.target.name]
    setRunData({...runData, [e.target.name]: !prev})
  }

  const handleDateChange = (e) => {
    setRunData({...runData, [e.target.name]: new Date()})
  }



  //form validate
  const [validated, setValidated] = useState(false);

  const handleSubmit = async (e) => {
    const form = e.currentTarget;

    if (form.checkValidity() === false) {
      e.preventDefault();
      e.stopPropagation();
    }
    e.preventDefault();

    setValidated(true)

    createRun(runData);
    setRunData({    
    name: "",
    description: "",
    location: "",
    distance: "",
    time: `${new Date().getHours()}:${new Date().getMinutes()}`,
    date: new Date(),
    file: "" })
    navigate('/profile');

    // if (form.checkValidity() === true) {
    //   const create = async (e) => {
    //     e.preventDefault();
    //     const status = await createRun(
    //       user.id,
    //       name,
    //       description,
    //       address,
    //       distance,
    //       time,
    //       date,
    //       coords.lat,
    //       coords.lng,
    // //       file
    //     );
    //     if (status) navigate("/profile");
    //   };
    // }
  };


  const datePick = () => {
    return (
      <Form.Group controlId="date" className="mb-3">
        <Form.Label>Date</Form.Label>
        <DatePicker
          required
          name="date"
          selected={runData.date}
          onChange={handleDateChange}
          //onChange={(date) => setDate(date)}
          // key={date}
          // label={label}
          // value={label}
          // className="date-picker"
          // selected={runData.date === label}
          // onChange={handleChange}
        />
      </Form.Group>
    );
  };

  const distanceSelector = () => {
    return (
      <div className="mb-3">
        {[2, 5, 10].map((label) => {
          return (
            <Form.Check
              required
              key={label}
              inline
              type="radio"
              id={`inline-radio-1`}
              label={`${label}k`}
              value={label}
              name="distance"
              checked={runData.distance === label}
              onChange={handleChange}
            />
          );
        })}
      </div>
    );
  };

  return (

    <Form className="form-container" encType="multipart/form-data" validated={validated} onSubmit={handleSubmit}>

      <div className="form-container-text">
        <Form.Text as="h3">Create a Run</Form.Text>
        <Form.Text as="p">
          Don't see a run event near you? Just tell us where and when and the
          rest is on us.
        </Form.Text>
      </div>
      <FloatingLabel controlId="name" label="Name" className="mb-3">
        <Form.Control
          required
          type="text"
          placeholder="Name"
          name="name"
          value={runData.name}
          onChange={handleChange}
        />
        <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        <Form.Control.Feedback type="invalid">
          Enter a name for the run.
        </Form.Control.Feedback>
      </FloatingLabel>
      <FloatingLabel
        controlId="description"
        label="Description"
        className="mb-3"
      >
        <Form.Control
          required
          as="textarea"
          type="text"
          name="description"
          placeholder="Description"
          value={runData.description}
          onChange={handleChange}
        />
        <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        <Form.Control.Feedback type="invalid">
          Write a short description including directions, necessary information,
          etc.
        </Form.Control.Feedback>
      </FloatingLabel>

      <FloatingLabel
        controlId="location"
        label="Where will this run be held?"
        className="mb-3"
      >
        <AutoComplete
          setAddress={setAddress}
          setPlaceName={setPlaceName}
          setCoords={setCoords}

        />
        <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        <Form.Control.Feedback type="invalid">
          Enter a valid address.
        </Form.Control.Feedback>
      </FloatingLabel>

      <Form.Group controlId="distance" className="mb-3">
        <Form.Label>Distance</Form.Label>
        {distanceSelector()}
      </Form.Group>

      <Row>
        <Col>{datePick()}</Col>
        <Col>
          <Form.Group controlId="time" className="mb-3">
            <Form.Label>Time</Form.Label>
            <Form.Control
              required
              type="time"
              name="time"
              placeholder="Time"
              value={runData.time}
              onChange={handleChange}
            />
            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            <Form.Control.Feedback type="invalid">
              Enter a valid time.
            </Form.Control.Feedback>
          </Form.Group>
        </Col>
      </Row>
      <Form.Group controlId="formFileLg" className="mb-3">
        <Form.Label>Upload an image</Form.Label>
        <Form.Control
          required
          name="file"
          type="file"
          onChange={handleChange}
        />
        <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        <Form.Control.Feedback type="invalid">
          Upload an image for this run.
        </Form.Control.Feedback>
      </Form.Group>
      <Button variant="primary" type="submit">
        Create
      </Button>
    </Form>
  );
}
