import { atom } from "recoil";
import { useSetRecoilState } from "recoil";
import { useEffect } from "react";

const userCoordinatesAtom = atom({
  key: "userCoordinatesAtom",
  default: { lat: null, lng: null},
});

export default userCoordinatesAtom;

const setUserCoords = useSetRecoilState(userCoordinatesAtom);

  useEffect(() => {
    const success = (pos) => {
      let coord = pos.coords;
      setUserCoords({ lat: coord.coords, lng: coord.longitude });
    }

    const error = (err) => {
      console.warn(`There was an error:${err.code}, ${err.message}`)
    }

    return navigator.geolocation.getCurrentPosition(success, error);

  }, [setUserCoords]);


