const key = process.env.REACT_APP_MAP_API_KEY;

const defaultMap = {
  center: {
    lat: 43,
    lgn: -80
  },
  zoom: 7
};

return (
  <div style={{height: '100vh', width: '100%'}}>
     <GoogleMapReact
      bootstrapURLKeys={{key: key}}
      defaultCenter={defaultMap.center}
      defaultZoom={defaultMap.zoom}
      >
    </GoogleMapReact>
  </div>

);
};
