import axios from 'axios';
import React, { useState, useEffect } from 'react';

//hook for getting runs
//hook for logged in user
//hook for runner only runs
//hook for planner runs
//axios route change based on user id



export default class RunList extends React.Component {
  state = {
    runs: []
  }

  componentDidMount() {
    axios.get('/api/runs/:id') 
    .then(res => {
      const run = res.data;
      this.setState({ run });
    })
  }


  render() {
    console.log(this.state);

    return (
      <ul>
        {
          this.state.run.map(r => 
            <li key={r.id}>{r.location}</li>
            )
        }
      </ul>
    )
  }

}

